Scripts.OnLoad:
self.itemButtons = {
    self.MainItem,
    self.ListItem1,
    self.ListItem2,
    self.ListItem3,
    self.ListItem4,
    self.ListItem5,
}
for i=1,#self.itemButtons do
    self.itemButtons[i].ID = i
end
self:SetScale(0.85)
self.bgLeft:ClearAllPoints();
self.bgLeft:SetTexCoord(0.25, 0.50, 0.25, 0.50);
self.bgLeft:SetWidth(64);
self.bgLeft:SetHeight(64);
self.bgLeft:SetPoint("TOPLEFT", -5, 0);
self.bgRight:ClearAllPoints();
